import 'package:get_storage/get_storage.dart';import 'package:ysh_fina/app/storage/storage.dart';/// A utility class for handling app storage using GetStorage.class AppStorage {  /// Instance of GetStorage to handle persistent storage.  static GetStorage storage = GetStorage();  ///To Set accessToken Name/  static Future<void> setUserAccessToken(String userAccessToken) async {    await storage.write(StorageConstant.accessTokenStorage, userAccessToken);  }  ///To Get Access Token of Current User  static String getAccessToken() {    return storage.read(StorageConstant.accessTokenStorage) ?? "";  }  /// for set  ask aura first tine  static Future<void> setIsLogin(bool data) {    return storage.write(StorageConstant.isLogin, data);  }  /// get ask aura first time  static bool getIsLogin() {    return storage.read(StorageConstant.isLogin) ?? false;  }  /// clear storage  static Future<void> clearStorage() async {    setIsLogin(false);  }//Get from Storage}