import 'package:flutter/material.dart';class AppBackground extends StatelessWidget {  final Widget child;  final Color? backgroundColor;  final bool resizeToAvoidBottomInset;  final bool extendBody;  final bool extendBodyBehindAppBar;  final PreferredSizeWidget? appBar;  final Widget? bottomNavigationBar;  final Widget? floatingActionButton;  final FloatingActionButtonLocation? floatingActionButtonLocation;  final List<BoxShadow>? boxShadow;  final EdgeInsets? padding;  final bool safeAreaTop;  final bool safeAreaBottom;  final Color? statusBarColor;  const AppBackground({    Key? key,    required this.child,    this.backgroundColor,    this.resizeToAvoidBottomInset = true,    this.extendBody = false,    this.extendBodyBehindAppBar = false,    this.appBar,    this.bottomNavigationBar,    this.floatingActionButton,    this.floatingActionButtonLocation,    this.boxShadow,    this.padding,    this.safeAreaTop = true,    this.safeAreaBottom = false,    this.statusBarColor,  }) : super(key: key);  @override  Widget build(BuildContext context) {    Widget content = child;    // Apply padding if provided    if (padding != null) {      content = Padding(        padding: padding!,        child: content,      );    }    // Apply SafeArea    content = SafeArea(      top: safeAreaTop,      bottom: safeAreaBottom,      child: content,    );    // Create the main container    if (boxShadow != null) {      content = Container(        decoration: BoxDecoration(          color: backgroundColor ?? Theme.of(context).scaffoldBackgroundColor,          boxShadow: boxShadow,        ),        child: content,      );    }    return Scaffold(      backgroundColor: backgroundColor ?? Theme.of(context).scaffoldBackgroundColor,      resizeToAvoidBottomInset: resizeToAvoidBottomInset,      extendBody: extendBody,      extendBodyBehindAppBar: extendBodyBehindAppBar,      appBar: appBar,      body: content,      bottomNavigationBar: bottomNavigationBar,      floatingActionButton: floatingActionButton,      floatingActionButtonLocation: floatingActionButtonLocation,    );  }}